import{_ as s,o as t,c as i,a2 as a}from"./chunks/framework.B72hu1JJ.js";const k=JSON.parse('{"title":"Client Events","description":"","frontmatter":{},"headers":[],"relativePath":"docs/core/events.md","filePath":"docs/core/events.md"}'),n={name:"docs/core/events.md"};function l(r,e,h,o,d,p){return t(),i("div",null,[...e[0]||(e[0]=[a('<h1 id="client-events" tabindex="-1">Client Events <a class="header-anchor" href="#client-events" aria-label="Permalink to &quot;Client Events&quot;">​</a></h1><p>Z-Core exposes a set of server → client events that other resources can trigger. These handle in-game actions like notifications, spectating, healing, and spawning vehicles.</p><p>All events below are triggered <strong>server-side</strong> using <code>TriggerClientEvent</code>.</p><hr><h2 id="notification" tabindex="-1">Notification <a class="header-anchor" href="#notification" aria-label="Permalink to &quot;Notification&quot;">​</a></h2><h3 id="z-core-client-notify" tabindex="-1"><code>z-core:client:notify</code> <a class="header-anchor" href="#z-core-client-notify" aria-label="Permalink to &quot;`z-core:client:notify`&quot;">​</a></h3><p>Sends a chat-box notification to a player.</p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:notify&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source, message, type)</span></span></code></pre></div><table tabindex="0"><thead><tr><th>Param</th><th>Type</th><th>Values</th></tr></thead><tbody><tr><td><code>message</code></td><td><code>string</code></td><td>The notification text</td></tr><tr><td><code>type</code></td><td><code>string</code></td><td><code>&#39;success&#39;</code> | <code>&#39;error&#39;</code> | <code>&#39;info&#39;</code></td></tr></tbody></table><p><strong>Example:</strong></p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// From your server script</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:notify&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;You have been healed.&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;success&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:notify&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;Action denied.&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;error&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span></code></pre></div><div class="tip custom-block"><p class="custom-block-title">Use player.notify() instead</p><p>If you already have a <code>ZPlayer</code> object, calling <code>player.notify(msg, type)</code> is equivalent and slightly cleaner.</p></div><hr><h2 id="health-status" tabindex="-1">Health &amp; Status <a class="header-anchor" href="#health-status" aria-label="Permalink to &quot;Health &amp; Status&quot;">​</a></h2><h3 id="z-core-client-heal" tabindex="-1"><code>z-core:client:heal</code> <a class="header-anchor" href="#z-core-client-heal" aria-label="Permalink to &quot;`z-core:client:heal`&quot;">​</a></h3><p>Fully restores the player&#39;s health and armor.</p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:heal&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source)</span></span></code></pre></div><p>No additional parameters.</p><p><strong>What it does client-side:</strong></p><ul><li>Sets health to maximum (<code>SetEntityHealth</code>)</li><li>Sets armor to maximum (<code>SetPedArmour</code>)</li></ul><hr><h3 id="z-core-client-revive" tabindex="-1"><code>z-core:client:revive</code> <a class="header-anchor" href="#z-core-client-revive" aria-label="Permalink to &quot;`z-core:client:revive`&quot;">​</a></h3><p>Revives a downed player. Brings them back from a dead/downed state and restores full health and armor.</p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:revive&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source)</span></span></code></pre></div><div class="info custom-block"><p class="custom-block-title">Framework dependency</p><p>If your framework uses a custom death/injury system (e.g. esx_ambulancejob, ps-ambulance), you may need to hook into those events additionally. This event handles the raw GTA state only.</p></div><hr><h2 id="admin-controls" tabindex="-1">Admin Controls <a class="header-anchor" href="#admin-controls" aria-label="Permalink to &quot;Admin Controls&quot;">​</a></h2><h3 id="z-core-client-freeze" tabindex="-1"><code>z-core:client:freeze</code> <a class="header-anchor" href="#z-core-client-freeze" aria-label="Permalink to &quot;`z-core:client:freeze`&quot;">​</a></h3><p>Toggles the player&#39;s frozen state. Calling it again unfreezes the player.</p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:freeze&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source)</span></span></code></pre></div><p>No additional parameters. The freeze state is a toggle — send again to unfreeze.</p><hr><h3 id="z-core-client-spectate" tabindex="-1"><code>z-core:client:spectate</code> <a class="header-anchor" href="#z-core-client-spectate" aria-label="Permalink to &quot;`z-core:client:spectate`&quot;">​</a></h3><p>Puts the calling player into spectate mode on the target player, or exits spectate if already spectating.</p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:spectate&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source, targetSource)</span></span></code></pre></div><table tabindex="0"><thead><tr><th>Param</th><th>Type</th><th>Description</th></tr></thead><tbody><tr><td><code>targetSource</code></td><td><code>number</code></td><td>Server ID of the player to spectate</td></tr></tbody></table><p><strong>Example:</strong></p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// Admin (source = 1) spectates player (targetSource = 42)</span></span>\n<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:spectate&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">1</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">42</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span></code></pre></div><hr><h2 id="vehicles" tabindex="-1">Vehicles <a class="header-anchor" href="#vehicles" aria-label="Permalink to &quot;Vehicles&quot;">​</a></h2><h3 id="z-core-client-spawnvehicle" tabindex="-1"><code>z-core:client:spawnVehicle</code> <a class="header-anchor" href="#z-core-client-spawnvehicle" aria-label="Permalink to &quot;`z-core:client:spawnVehicle`&quot;">​</a></h3><p>Spawns a vehicle at the player&#39;s current position and facing direction.</p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:spawnVehicle&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source, model)</span></span></code></pre></div><table tabindex="0"><thead><tr><th>Param</th><th>Type</th><th>Description</th></tr></thead><tbody><tr><td><code>model</code></td><td><code>string</code></td><td>Vehicle model name (e.g. <code>&#39;adder&#39;</code>, <code>&#39;police&#39;</code>, <code>&#39;zentorno&#39;</code>)</td></tr></tbody></table><p><strong>Example:</strong></p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:spawnVehicle&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;adder&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span></code></pre></div><p>Z-Core ships with a list of 900+ vanilla GTA vehicle models available through the admin panel&#39;s <strong>Assets</strong> tab. Any model string from that list works here.</p><hr><h2 id="nui-events-admin-panel" tabindex="-1">NUI events (admin panel) <a class="header-anchor" href="#nui-events-admin-panel" aria-label="Permalink to &quot;NUI events (admin panel)&quot;">​</a></h2><p>These events control the Z-Admin UI. You generally don&#39;t call these directly — they&#39;re used internally by Z-Core&#39;s server → client bridge. They&#39;re documented here for completeness.</p><h3 id="set-visible" tabindex="-1"><code>SET_VISIBLE</code> <a class="header-anchor" href="#set-visible" aria-label="Permalink to &quot;`SET_VISIBLE`&quot;">​</a></h3><p>Shows or hides the admin panel UI.</p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:setVisible&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source, visible: boolean)</span></span></code></pre></div><h3 id="open-report-modal" tabindex="-1"><code>OPEN_REPORT_MODAL</code> <a class="header-anchor" href="#open-report-modal" aria-label="Permalink to &quot;`OPEN_REPORT_MODAL`&quot;">​</a></h3><p>Opens the in-game report/ticket creation modal for a player.</p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:openReportModal&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source)</span></span></code></pre></div><h3 id="open-player-tickets" tabindex="-1"><code>OPEN_PLAYER_TICKETS</code> <a class="header-anchor" href="#open-player-tickets" aria-label="Permalink to &quot;`OPEN_PLAYER_TICKETS`&quot;">​</a></h3><p>Opens the player&#39;s own ticket view.</p><div class="language-ts vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ts</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:openPlayerTickets&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source)</span></span></code></pre></div><hr><h2 id="support-tickets-player-commands" tabindex="-1">Support tickets (player commands) <a class="header-anchor" href="#support-tickets-player-commands" aria-label="Permalink to &quot;Support tickets (player commands)&quot;">​</a></h2><p>Z-Core registers two commands for players to interact with the support ticket system:</p><table tabindex="0"><thead><tr><th>Command</th><th>Description</th></tr></thead><tbody><tr><td><code>/report [message]</code></td><td>Creates a new support ticket with the given message</td></tr><tr><td><code>/ticket</code></td><td>Opens the player&#39;s ticket history in the UI</td></tr></tbody></table><p>These commands are registered by <code>TicketManager</code> on resource start. No configuration needed.</p><hr><h2 id="server-side-events-internal" tabindex="-1">Server-side events (internal) <a class="header-anchor" href="#server-side-events-internal" aria-label="Permalink to &quot;Server-side events (internal)&quot;">​</a></h2><p>These events are listened to by Z-Core internally. You should not call them directly unless you&#39;re building deep integrations.</p><table tabindex="0"><thead><tr><th>Event</th><th>Triggered by</th><th>Purpose</th></tr></thead><tbody><tr><td><code>playerConnecting</code></td><td>FiveM built-in</td><td>Ban check on connection</td></tr><tr><td><code>z-core:nui:*</code></td><td>Admin panel NUI callbacks</td><td>All admin panel actions</td></tr><tr><td><code>z-core:getJobs</code></td><td>Admin panel</td><td>Load job list</td></tr><tr><td><code>z-core:requestTickets</code></td><td>Admin panel</td><td>Load all tickets</td></tr><tr><td><code>z-core:submitReport</code></td><td>Player command</td><td>Create ticket</td></tr><tr><td><code>z-core:enableMap</code></td><td>Admin panel</td><td>Start live map stream</td></tr><tr><td><code>z-core:disableMap</code></td><td>Admin panel</td><td>Stop live map stream</td></tr><tr><td><code>z-core:mapAction</code></td><td>Admin panel</td><td>Teleport/kill/heal via map</td></tr></tbody></table><hr><h2 id="using-events-from-lua" tabindex="-1">Using events from Lua <a class="header-anchor" href="#using-events-from-lua" aria-label="Permalink to &quot;Using events from Lua&quot;">​</a></h2><p>All client events work identically from Lua:</p><div class="language-lua vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">lua</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">-- Heal a player</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:heal&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source)</span></span>\n<span class="line"></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">-- Notify a player</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:notify&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;Busted!&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;error&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span>\n<span class="line"></span>\n<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">-- Spawn a vehicle for a player</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">TriggerClientEvent</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z-core:client:spawnVehicle&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, source, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;police&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span></code></pre></div>',72)])])}const g=s(n,[["render",l]]);export{k as __pageData,g as default};
